// SPDX-License-Identifier: MIT
pragma solidity 0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

pragma experimental ABIEncoderV2;

contract Ndu is Ownable {

    uint companyCount;
    // address owner;
    address admin;
    address public registerContract;
    uint public balance;
    
    constructor() public {
        admin = msg.sender;
    }

    // function receive() external payable {
    //     balance = msg.value;
    // }
    
    
    // This represents a single Company 
    struct Company {
        uint id;
        string Name;
        string ISOnumber;
        string ipfsHashOfCompanyDetails;
    }

    // This represents a single drug
    struct Drug {
        string ipfsHashOfDrugDetails;
    }



    Company[] public registerdCompanies;

    // This keeps track of a registered company Id
    mapping(uint => Company) public companyId;

    

    // companies variable stores a 'Company' struct for each possible address
    // mapping(address => Company) public companies;




    // keep track of registered serial
    mapping(uint => bool) public storedSerial;


    // keep track of products registered by a company
    mapping(uint => Company ) public drugProduced;

    // keep track of Scanned Products
    mapping(uint => bool) public serialScanned;


    // keep track of drug details
    mapping(uint => Drug) public drugDetails;


    modifier onlyAdmin(){
        require(msg.sender == admin, "only admin can call");
        _;
    }
    
    

    function register(uint _id, string memory _name, string memory _isonumber, string memory _ipfsHashOfCompanyDetails ) public {
        require(msg.sender == registerContract, "Only Register Contract can call");
        // companies[_callerCompanyAddress] = Company(_id, _name, _isonumber, _ipfsHashOfCompanyDetails);
        companyId[_id] = Company(_id, _name, _isonumber, _ipfsHashOfCompanyDetails);
        registerdCompanies.push(Company(_id, _name, _isonumber, _ipfsHashOfCompanyDetails));
        // registered[_callerCompanyAddress] = true;
    }

     

    function registerSerial(uint _id, uint _serialNumber, string memory _ipfsHashOfDrugDetails) public {
        require(msg.sender == registerContract, "Only Register Contract can call");
        // require(registered[_caller], "company address not registered");
        Company storage company = companyId[_id];
        drugProduced[_serialNumber] = company;
        drugDetails[_serialNumber] = Drug(_ipfsHashOfDrugDetails);
        storedSerial[_serialNumber] = true;
    }



    function scanProduct(uint _serialNumber ) public view  returns(Company memory, Drug memory)

        {
            require(storedSerial[_serialNumber], "serial is not registered ");
            require(!serialScanned[_serialNumber], "drug has been scanned");
            Company memory company = drugProduced[_serialNumber];
            Drug memory drug = drugDetails[_serialNumber];
            
            return (company, drug) ;
                
        }
        
        
        
    function updateStatus(uint _serialNumber) public {
        serialScanned[_serialNumber] = true;
    }
    
    function checkStatus(uint _serialNumber) public  {
        require(storedSerial[_serialNumber], "serial not registered ");
        require(!serialScanned[_serialNumber], "drug has been scanned");
    }

    

    function setAdmin(address _newAdmin) public onlyAdmin {
        admin = _newAdmin;
    }

    function setRegisterContract(address _registerContract) public onlyAdmin {
        registerContract = _registerContract;
    }

  

}


contract Register {

    IERC20 governanceToken;
    uint public minimumTokenRequired;
    address public admin;
    Ndu public ndu;
    
    // this keeps track of company address registerd
    mapping(address => bool) public registered;
    
    
    
    mapping(address => uint) public companyId;

    // events
    event companyRegister(address indexed _companyAddress, string _name, string _isonumber, string _ipfsHashOfCompanyDetails );
    event drugRegister(address indexed _sender, uint indexed _serialNumber, string ipfsHashOfDrugDetails);




    constructor(address _governanceToken, address payable _nduContract,  uint _minimumTokenRequired) public {
        governanceToken = IERC20(_governanceToken);
        admin = msg.sender;
        ndu = Ndu(_nduContract);
        minimumTokenRequired = _minimumTokenRequired;
    }


    modifier onlyAdmin(){
        require(msg.sender == admin, "only admin can call");
        _;
    }





    function registerCompany( uint256 _id, string memory _name, string memory _isonumber, string memory _ipfsHashOfCompanyDetails ) public  {
        require(governanceToken.balanceOf(msg.sender) >= minimumTokenRequired, "insufficient balance");
        governanceToken.transferFrom(msg.sender, admin, minimumTokenRequired);
        
        ndu.register( _id,_name,_isonumber,_ipfsHashOfCompanyDetails);

        companyId[msg.sender] = _id;
        registered[msg.sender] = true;

        emit companyRegister(msg.sender, _name, _isonumber, _ipfsHashOfCompanyDetails);
    }
    




    function registerSerialNumber( uint _serialNumber, string memory _ipfsHashOfDrugDetails ) public {
        require(registered[msg.sender], "Caller not registered");
        uint _id = companyId[msg.sender];
         ndu.registerSerial(_id,_serialNumber,_ipfsHashOfDrugDetails);

        emit drugRegister(msg.sender, _serialNumber, _ipfsHashOfDrugDetails);
    }



    function setMinimumTokenRequired(uint _minimumTokenrequired) public onlyAdmin {
        minimumTokenRequired = _minimumTokenrequired;
    }
}


contract ScanProduct {
    IERC20 public governanceToken;
    Ndu public ndu;
    address public admin;
    
    constructor(address payable _nduContract, address _governanceToken) public {
        governanceToken = IERC20(_governanceToken);
        ndu = Ndu(_nduContract);
        admin = msg.sender;
        
        
    }
    
    function Scan(uint _serialNumber) public {
        // scan Product
        ndu.scanProduct(_serialNumber);
        // update scan status
        ndu.updateStatus(_serialNumber);
    }
}
